if (typeof window !== undefined) {
  // Slides close handler
  $(document).on("click", "#comp-message-bar", function (e) {
    const target = e.target.closest(".close");
    const targetParent = e.target.closest(".mySlides");
    if (target && targetParent) {
      targetParent.remove();
    }
  });
  // Rotate Slides
  (function carousel(slideIndex) {
    const allSlides = document.querySelectorAll("#comp-message-bar .mySlides");
    allSlides.forEach((slide) => {
      slide.style.display = "none";
    });

    if (!allSlides[slideIndex]) {
      slideIndex = 0;
    }

    if (allSlides.length <= 0) {
      setTimeout(carousel, 5000, slideIndex);
      return;
    }

    allSlides[slideIndex].style.display = "block";
    slideIndex = (slideIndex + 1) % allSlides.length;

    // Rotate the slide every 5 seconds
    setTimeout(carousel, 5000, slideIndex);
  })(0);
}

